Item.method_code:
var email_lang = "English"; //default to english
var oRadio = document.forms[0].elements["email_language"];
for(var i = 0; i < oRadio.length; i++)
{
   if(oRadio[i].checked)
   {
      email_lang = oRadio[i].value;
   }
}

var retVal=new Array();
retVal["email_language"] = email_lang;
window.returnValue = retVal;
window.close();
